import React, { useState } from 'react';
import { Modal, Form, Button, Card, Row, Col, Alert, Spinner } from 'react-bootstrap';
import { useUser } from '../context/UserContext';
import emailjs from 'emailjs-com';

const PaymentModal = ({ show, onHide, tour }) => {
  const { user, bookTour } = useUser();
  const [paymentMethod, setPaymentMethod] = useState('credit-card');
  const [cardInfo, setCardInfo] = useState({
    cardNumber: '',
    expiryDate: '',
    cvv: '',
    cardHolder: ''
  });
  const [bankInfo, setBankInfo] = useState({
    bankName: 'vietcombank',
    accountNumber: ''
  });
  const [isProcessing, setIsProcessing] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  const handleCardChange = (e) => {
    const { name, value } = e.target;
    setCardInfo(prev => ({ ...prev, [name]: value }));
  };

  const handleBankChange = (e) => {
    const { name, value } = e.target;
    setBankInfo(prev => ({ ...prev, [name]: value }));
  };

  const sendConfirmationEmail = async (bookingDetails) => {
    try {
      const templateParams = {
        to_name: user.name,
        to_email: user.email,
        tour_name: tour.name,
        tour_location: tour.location,
        departure_date: new Date(tour.departureDate).toLocaleDateString('vi-VN'),
        amount: tour.price.toLocaleString('vi-VN'),
        booking_id: bookingDetails.id,
        payment_method: bookingDetails.paymentMethod === 'credit-card' ? 'Th·∫ª t√≠n d·ª•ng' : 'Chuy·ªÉn kho·∫£n ng√¢n h√†ng'
      };

      // C·∫•u h√¨nh EmailJS (c·∫ßn ƒëƒÉng k√Ω t·∫°i emailjs.com)
      await emailjs.send(
        'YOUR_SERVICE_ID', // Thay b·∫±ng Service ID c·ªßa b·∫°n
        'YOUR_TEMPLATE_ID', // Thay b·∫±ng Template ID c·ªßa b·∫°n
        templateParams,
        'YOUR_PUBLIC_KEY' // Thay b·∫±ng Public Key c·ªßa b·∫°n
      );
      
      console.log('Email x√°c nh·∫≠n ƒë√£ ƒë∆∞·ª£c g·ª≠i th√†nh c√¥ng!');
