// src/components/TourCard.js
import React from 'react';
import { Card, Button, Badge } from 'react-bootstrap';
import { useUser } from '../context/UserContext';
import { useTours } from '../context/TourContext';

const TourCard = ({ tour, onEdit, showActions = true }) => {
  const { isAuthenticated } = useUser();
  const { deleteTour } = useTours();

  const handleDelete = () => {
    if (window.confirm('B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën x√≥a tour n√†y?')) {
      deleteTour(tour.id);
    }
  };

  return (
    <Card className="h-100 shadow-sm">
      <Card.Img 
        variant="top" 
        src={tour.image} 
        style={{ height: '200px', objectFit: 'cover' }}
      />
      <Card.Body className="d-flex flex-column">
        <Card.Title>{tour.name}</Card.Title>
        <Card.Text>
          <strong>üìç {tour.location}</strong><br />
          {tour.description}
        </Card.Text>
        <Card.Text>
          <strong>üóìÔ∏è Kh·ªüi h√†nh:</strong> {new Date(tour.departureDate).toLocaleDateString('vi-VN')}
        </Card.Text>
        <Card.Text>
          <strong>üí∞ Gi√°:</strong> <span className="text-primary">{tour.price.toLocaleString('vi-VN')} VNƒê</span>
        </Card.Text>
        <div className="mb-2">
          <Badge bg="warning" text="dark">
            ‚≠ê {tour.rating?.toFixed(1) || 'Ch∆∞a c√≥ ƒë√°nh gi√°'}
          </Badge>
        </div>
        <div className="mt-auto">
          {showActions && isAuthenticated && (
            <div className="d-flex gap-2">
              <Button variant="outline-primary" size="sm" onClick={() => onEdit(tour)}>
                S·ª≠a
              </Button>
              <Button variant="outline-danger" size="sm" onClick={handleDelete}>
                X√≥a
              </Button>
            </div>
          )}
        </div>
      </Card.Body>
    </Card>
  );
};

export default TourCard;
